import Axios from 'axios';

export const createNewTask = (indexCol, listId) => {
  console.log(indexCol, listId)
  Axios.post('http://localhost:3001/api/insert', {
    indexCol: indexCol,
    listId: listId
  }).then(response => { return(response.data.insertId) });
};

export const getTasks = () => {
  return(Axios.get('http://localhost:3001/api/get/all'));
};

export const deleteTask = (id, index, listId) => {
  Axios.delete(`http://localhost:3001/api/delete/${id}`);
  Axios.put('http://localhost:3001/api/update/decrementIndexes', {
    index: index,
    listId: listId
  });
};

export const updateTask = (id, task) => {
  Axios.put('http://localhost:3001/api/update/task', {
    id: id,
    task: task
  });
};

export const swapTaskIndex = (id1, id2, index1, index2) => {
  Axios.put('http://localhost:3001/api/update/swapTaskIndex', {
    id1: id1,
    index1: index1,
    id2: id2,
    index2: index2
  })
}
