import React, {useEffect, useState} from "react";

import * as ApiClient from "../../../../ApiClient";

import "../new.css";

const ToDo = ({todo}) => {

  const currentTodo = todo

  const [isOpen, setIsOpen] = useState(false);
  const [isDeleted, setIsDeleted] = useState(false);
  const [updateBar, setUpdateBar] = useState("");

  const openOrClose = () => {
    isOpen ? setIsOpen(false) : setIsOpen(true);
  };

  const updateTask = (updatedTask) => {
    ApiClient.updateTask(currentTodo.id, updatedTask);
    setUpdateBar("");
    setIsOpen(false);
    console.log(updateBar + "i");
  };

  const deleteTask = () => {
    ApiClient.deleteTask(currentTodo.id);
    setIsDeleted(true);
  };

  return (
    <div>
      {isDeleted ? 
        <div></div>
        :
          isOpen ? 
          <div className="Open-Card"> 
            <input 
              type="text" 
              className="Update-Task"
              id={currentTodo.id} 
              onChange={(e) => {setUpdateBar(e.target.value)}}
              onKeyUp={(e) => {if (e.key === "Enter") { updateTask(updateBar) }}}
              defaultValue={currentTodo.task}>
            </input>
            <button className="Delete-Task" onClick={deleteTask}>Delete</button>
          </div>
          :
          <button className={`${currentTodo.task == null ? "Empty-Card" :"Closed-Card"}`} onClick={openOrClose}> 
            {isOpen ? 'true' : 'false'} 
            {currentTodo.task == null ? 'Untitled' : currentTodo.task}    
          </button>    
      }

    </div>

  );
};
/** 
*/
export default ToDo;
