import React, {useEffect, useState} from "react";

import * as ApiClient from "../../../../ApiClient";

import "../new.css";

const ToDo = ({todo}) => {

  const [isOpen, setIsOpen] = useState(false);
  const [isDeleted, setIsDeleted] = useState(false);
  const [updateBar, setUpdateBar] = useState("");

  const openOrClose = () => {
    isOpen ? setIsOpen(false) : setIsOpen(true);
  };

  const updateTask = (updateBar) => {
    ApiClient.updateTask(todo.id, updateBar);
    console.log(updateBar);
  };

  const deleteTask = () => {
    ApiClient.deleteTask(todo.id);
    setIsDeleted(true);
  };

  useEffect(() => {
    document.addEventListener("keyup", (e) => {

      if (document.activeElement.id==todo.id && e.code == "Enter") {
        updateTask();
      };
    });
  }, []);

  return (
    <div>
      {isDeleted ? 
        <div></div>
        :
          isOpen ? 
          <div className="Open-Card"> 
            <input 
              type="text" 
              className="Update-Task"
              id={todo.id} 
              onChange={(e) => {setUpdateBar(e.target.value);}}
              defaultValue={todo.task}>
            </input>
            <button className="Delete-Task" onClick={deleteTask}>Delete</button>
          </div>
          :
          <button className={`${todo.task == null ? "Empty-Card" :"Closed-Card"}`} onClick={openOrClose}> 
            {isOpen ? 'true' : 'false'} 
            {todo.task == null ? 'Untitled' : todo.task}    
          </button>    
      }

    </div>

  );
};
/** 
*/
export default ToDo;
